$('#demoInput').keypress(function (e) {
    if (e.which == 13) {
        sendEvent('demo-chat', {message: $('#demoInput').val()});
        $('#demoInput').val('');
    }
});

handleEvent('demo-chat', function (e, data) {
    $('#demoChat').append('<div><em>' + data.from.name + '</em>: ' + data.message + '</div>').scrollTop(10000);
});
